mapper.select:
select ed.*, ep.name as config_name, ep.title as config_title, ep.type as config_type, ep.applies_to as config_appliesTo, ep.tips, ep.masked, ep.required, ep.default_value, ep.allowed_values, ep.mode_required from t_engine_endpoint ed left join t_engine_ep_properties ep on ed.type = ep.endpoint_type
and ed.del_flag = false
and ed.name like '%'#{name}'%'
and ed.type = #{type}
and ed.mode = #{mode}
